import React from "react";
import '../StartACampaign.css';


const BasicStep = ({ handleChange }) => {
    return (
        <div>
            <fieldset>
                <div className="form-card">
                    <div className="row">
                        <div className="col-7">
                            <h2 className="fs-title">Account Information:</h2>
                        </div>
                        <div className="col-5">
                            <h2 className="steps">Step 1/3</h2>
                        </div>
                    </div> <label className="fieldlabels">Email: *</label> 
                    <input type="email" name="email" placeholder="Email Id" /> 
                    <label className="fieldlabels">Username: *</label> <input type="text" name="uname" placeholder="UserName" /> <label className="fieldlabels">Password: *</label> <input type="password" name="pwd" placeholder="Password" /> <label className="fieldlabels">Confirm Password: *</label> <input type="password" name="cpwd" placeholder="Confirm Password" />
                </div> <input type="button" name="next" className="next action-button" value="Next" />
            </fieldset>
        </div>


    )};

export default BasicStep;
