import React, { useEffect, useState } from 'react';
//import { Route } from 'react-router';
import { NavLink, useHistory, useParams } from 'react-router-dom';
import './CampaignDetails.css';
import './payment.css'
import './Discussion.css';
import StripeCheckout from "react-stripe-checkout";


const Payment = () => {

    const { id } = useParams();

    const [product, setProduct] = useState({
        name: "React from FB",
        price: 10,
        productBy: "facebook"
    });


    const history = useHistory();
    const [userData, setuserData] = useState({});
    const [project, setProject] = useState({});
    let data, data1;

    const calluserdataretrive = async () => {
        try {
            const res = await fetch('/what-we-do', {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                },
                credentials: "include"

            });

            const res1 = await fetch(`/projectdetails/${id}/payment`, {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                },
                credentials: "include"


            });


            data = await res.json();
            console.log(data);
            setuserData(data);
            console.log(userData);
            console.log(userData.email);
            if (res.status === 401) {
                const error = new Error(res.error);
                throw error;
            }
            data1 = await res1.json();
            console.log(data1)
            setProject(data1.project)
            console.log(project)

        } catch (err) {
            console.log(err);
            history.push('/login');
        }

    }

    useEffect(() => {
        calluserdataretrive();

    }, []);








    const makePayment = token => {
        const body = {
            token,
            product
        };
        const headers = {
            "Content-Type": "application/json"
        };

        return fetch(`/payment`, {
            method: "POST",
            headers,
            body: JSON.stringify(body)
        })
            .then(response => {
                console.log("RESPONSE ", response);
                const { status } = response;
                console.log("STATUS ", status);
            })
            .catch(error => console.log(error));
    };


    return (
        <>
            <div className="container" style={{ height: "100vh" }}>
                <div className="row justify-content-center">
                    <div className="top-div" id="part">
                        <div className="left-div" style={{ marginTop: "80px", float: "left" }}>
                            <span>YOU ARE CONTRIBUTING TO</span>
                            <h3>{project.ctitle}</h3>

                            <div>
                                <img style={{ width: "80px", height: "80px", borderRadius: "40px", marginRight: "15px" }}
                                    src="https://picsum.photos/100"
                                />
                            </div>
                            <div>
                                <span id="user-name">{userData.name}</span>
                                <p>backer </p>
                                <span id="user-name">{userData.email}</span>


                            </div>

                        </div>

                        <div className="top-div">
                            <div>
                                <input type="number" name="bamount" placeholder="Amount" />
                            </div>
                            <StripeCheckout
                                stripeKey="pk_test_51J6aoDKc0TZv9NwKZZSNcS3gVZ1MwVfm6VKsOo5OCxup4GQjNHXwlZAAK4GQr7QFmtiX8spQdkuqOzeTo5USbSM400IWNwUBtU"
                                token={makePayment}
                                name="Buy React"
                                amount={product.price * 100}
                            /* shippingAddress
                            billingAddress */
                            >
                                <button className="btn-large blue">
                                    Buy react is just {product.price} $
                                </button>
                            </StripeCheckout>
                        </div>
                    </div>
                </div>
            </div>


        </>
    );
};

export default Payment;
