import React, { useState } from 'react';
import './Start'
//import { Route } from 'react-router';

const StartACampaign = () => {
    const [values, setValues] = useState({
        first_name: "",
        last_name: "",
        gender: "",
        email: "",
        phone_number: "",
        city: "",
        state: "",
    });
    const [step, setStep] = useState(1);

    const nextStep = () => {
        if (step < 3) {
            setStep(step + 1);
        } else if (step === 3) {
            console.log(values);
        }
    };
    const prevStep = () => {
        if (step > 1) {
            setStep(step - 1);
        }
    };




    return (
        <div className="container-fluid">
            <div className="row justify-content-center">
                <div className="col-11 col-sm-9 col-md-7 col-lg-6 col-xl-5 text-center p-0 mt-3 mb-2">
                    <div className="card px-0 pt-4 pb-0 mt-3 mb-3">
                        <h2 id="heading">Sign Up Your User Account</h2>
                        <p>Fill all form field to go to next step</p>
                        <form id="msform">
                            <ul id="progressbar">
                                <li className="active" id="account"><strong>Account</strong></li>
                                <li id="personal"><strong>Personal</strong></li>
                                <li id="payment"><strong>Image</strong></li>
                                <li id="confirm"><strong>Finish</strong></li>
                            </ul>
                            <div className="progress">
                                <div className="progress-bar progress-bar-striped progress-bar-animated" role="progressbar" aria-valuemin="0" aria-valuemax="100"></div>
                            </div> <br/> 



                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StartACampaign;
