import React, { useEffect, useState } from 'react';
//import { Route } from 'react-router';
import { NavLink, useHistory, useParams } from 'react-router-dom';

import './Profile.css'


const Profile = () => {

    const [proimage, setProimage] = useState({
        proimage: "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
    })


    const imageHandler = (e) => {
        const reader = new FileReader();
        reader.onload = () => {
            if (reader.readyState === 2) {
                setProimage({ proimage: reader.result })
            }
        }
        reader.readAsDataURL(e.target.files[0])
    };

    return (
        <>

            <div className="Container">
                <div className="row justify-content-center">
                    <div className="col-13 col-sm-11 col-md-7 col-lg-7 col-xl-7 text-center p-0 mt-3 mb-2">
                        <div className="card vh-100 px-0 pt-4 pb-0 mt-3 mb-3">

                            <div className="page">
                                <div className="container">
                                    <h1 className="heading">Add your Image</h1>
                                    <div className="img-holder">
                                        <img src={proimage} alt="" id="img" className="img" />
                                    </div>
                                    <input type="file" accept="image/*" name="image-upload" id="input" onChange={imageHandler} />
                                    <div className="label">
                                        <label className="image-upload" htmlFor="input">
                                            <i className="material-icons">add_photo_alternate</i>
                                            Choose your Photo
                                        </label>
                                    </div>
                                </div>
                            </div>


                        </div>
                    </div>
                </div>

            </div>



        </>
    );

}

export default Profile;
