import React from "react";
import '../StartACampaign.css';


const Contents = ({ faqList, setfaqList }) => {

    const handleFAQ = (e, index) => {
        const { faq, data } = e.target;
        const list = [...faqList];
        list[index][faq] = data;
        setfaqList(list);
    };

    const handleRemoveClick = index => {
        const list = [...faqList];
        list.splice(index, 1);
        setInputList(list);
    };

    return (
        <div>
            <fieldset>
                <div className="form-card">
                    <div className="row">
                        <div className="col-7">
                            <h2 className="fs-title">Contents:</h2>
                        </div>
                        <div className="col-5">
                            <h2 className="steps">Step 2/3</h2>
                        </div>
                    </div>
                    <label className="fieldlabels">Story: *</label>
                    <textarea
                        className="form-control"
                        id="story"
                        style={{ "height": "200px" }}
                        name="story"

                    />
                    <div>
                        <label className="fieldlabels">FAQ</label>
                        {faqList.map((item, i) => {
                            return (

                                <div key={i}>
                                    <input type="text" name="question" placeholder="Question" value={item.question} onChange={handleFAQ} />
                                    <input type="text" name="answer" placeholder="Answer" value={item.answer} onChange={handleFAQ} />
                                    <input className="btn action-button" type="button" value="+Add" />
                                </div>
                            )

                        })}


                    </div>

                </div>

            </fieldset>
        </div>


    )
};

export default Contents;
