import React from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import '../../node_modules/bootstrap/dist/js/bootstrap.bundle';



function CampaignDetails() {
    return (

        <div className="container-fluid">

            <div className="row">
                <div className="about-img">
                    <div className="img-box inner-shadow">
                        <img src="img/profile-pic.jpg" className="outer-shadow" alt="profile-pic" />
                    </div>
                    <div className="social-links">
                        <a href="#" className="outer-shadow hover-in-shadow"><i className="fab fa-facebook-f"></i></a>
                        <a href="#" className="outer-shadow hover-in-shadow"><i className="fab fa-linkedin-in"></i></a>
                    </div>

                </div>
                <div className="about-info">
                    <p><span>Hi! My name is Most.Jannatul Ferdousi. I'm pursuing CSE at SUST, in Sylhet.</span>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,
                        quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                    </p>
                    <p>Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum</p>
                    <a href="cv.pdf" className="btn-1 outer-shadow hover-in-shadow">Download CV</a>
                    <a href="#" className="btn-1 outer-shadow hover-in-shadow">Hire Me</a>
                </div>
            </div>
        </div>

    );
}

export default CampaignDetails;
